using UnityEngine;
using System.Collections;
public class delitem : Story {
	GameObject [] itemUI;
	bool x=false;
	int ta=1;
	void OnCollisionEnter2D(Collision2D other){
		x=true;
	}
	void LateUpdate(){
		if(Input.GetKeyUp(KeyCode.Space)&&x){
			x=false;
			itemUI=GameObject.FindGameObjectsWithTag("itemUI");
			meet("A",this.gameObject.name+ta);
			if (ta <=1) {
				ta++;
			}
		}
	}
	void OnCollisionExit2D(Collision2D other){
		x=false;
	}
	protected override void otherthing(){
		menu.itemSave.gift=menu.Addnewitems(menu.itemSave,itemUI,"itemList","itemitem","itemitem","單人補血",2,
			""
			,"YY");
		Invoke ("del", 1);
	}
	void del(){
		Destroy (this.gameObject);
	}
}
